import React from "react";
import { useState, useEffect, useRef } from "react";

import {
  Grid,
  Box,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  IconButton,
  Card,
  CardActionArea,
  CardActions,
  CardContent,
  CardMedia
} from "@material-ui/core";
import Icon from "@mdi/react";
import {
  Star as StarIcon,
  StarBorder as StarOutlinedIcon,
  ShoppingCart as ShoppingCartIcon
} from "@material-ui/icons";
import {
  mdiFacebook as FacebookIcon,
  mdiInstagram as InstagramIcon,
  mdiTwitter as TwitterIcon
} from "@mdi/js";
import useStyles from "./styles";
import { yellow } from "@material-ui/core/colors/index";

//components
import Widget from "../../components/Widget";
import PageTitle from "../../components/PageTitle";
import { Typography, Link, Button } from "../../components/Wrappers";

//images
import img1 from "../../images/img1.jpg";
import img1_1 from "../../images/img1_1.jpg";
import img2 from "../../images/img2.jpg";
import img3 from "../../images/img3.jpg";
import img4 from "../../images/img4.jpg";
import img5 from "../../images/img5.jpeg";
import img6 from "../../images/img6.jpg";
import payment1 from "../../images/mastercard.svg";
import payment2 from "../../images/paypal.svg";
import payment3 from "../../images/visa.svg";
import payment4 from "../../images/aexpress.svg";

export const rows = [
  {
    id: 1,
    img: img1,
    hardware: "стэнд 20A-0004",
    hardware_status: "оффлайн",
    title: "Работа со стэндом - сенсором препятствий",
    subtitle: "Обнаружение препятствий и детектирование движений",
    description: "Используется уст-во с серийным номером 20A-004. Данное уст-во представялет собой стенд, оборудованный датчиками - измерителями расстояния.",
    technology: "Python, Arduino",
    price: 37,
    progress: 0,
    rating: 4.6,
    color: "primary",
    status: "Shipped",
    process: "100%"
  },
  {
    id: 2,
    img: img2,
    title: "Знакомство с виртуальной средой.",
    subtitle: "Управление роботом в виртульной среде.",
    description: "Данное событие представляет собой командное соревнование, в котором командам предстоит управлять роботом в виртуальной среде, подавая команды через код на языке программирования Python в совместном редакторе кода и выполняя поставленные в событии задачи, которые будут озвучены во время старта события.",
    technology: "Python",
    price: 37,
    progress: 100,
    rating: 5,
    color: "primary",
    status: "Shipped",
    process: "64%" 
  }
];

const Product = props => {
  const inputLabel = React.useRef(null);
  const [labelWidth, setLabelWidth] = React.useState(0);
  const classes = useStyles();
  const [size, setValues] = React.useState("");
  const handleChange = event => {
    setValues(event.target.value);
  };
  const [addSize, setAddSize] = React.useState(1);
  const handleChangeAddSize = event => {
    setAddSize(event.target.value);
  };

  const ws = useRef(null);

  useEffect(() => {
        ws.current = new WebSocket("ws://79.143.25.41:8080/gear");
        ws.current.binaryType = 'arraybuffer';
        ws.current.onclose = () => console.log("ws closed"); //devs - offline
        return () => {
            ws.current.close();
        };
    }, []);



  return (
    <>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Widget disableWidgetMenu noBodyPadding>
            <Grid container>
              <Grid item md={6} xs={12}>
                {!props.match.params.id ? (
                  <img
                    src={rows[0].img}
                    alt={rows[0].title}
                    style={{ width: "100%", minHeight: 400 }}
                  />
                ) : (
                  <img
                    src={rows[props.match.params.id - 1].img}
                    alt={rows[props.match.params.id - 1].title}
                    style={{ width: "100%", minHeight: 400 }}
                  />
                )}
              </Grid>
              <Grid item md={6} xs={12}>
                <Box
                  m={3}
