import React, { useState } from "react";
import {
  Grid,
  CircularProgress,
  TextField as Input
} from "@material-ui/core";
import { withRouter, useHistory } from "react-router-dom";

// styles
import useStyles from "./styles";

// logo
import logo from "./logo.svg";

// context
import { useUserDispatch, resetPassword, authError } from "../../context/UserContext";

//components
import { Button, Typography } from "../../components/Wrappers";

function Reset(props) {
  var classes = useStyles();
  const history = useHistory();
  // global
  var userDispatch = useUserDispatch();
  const [passwordValue, setPasswordValue] = useState("");
  const [confirmPasswordValue, setConfirmPasswordValue] = useState("");
  const [isLoading, setIsLoading] = useState(false);
    const checkPassword = () => {
    if (!isPasswordValid()) {
      if (!passwordValue) {
        authError("Password field is empty")(userDispatch);
      } else {
        authError("Passwords are not equal")(userDispatch);
      }
      setTimeout(() => {
        authError()(userDispatch);
      }, 3 * 1000)
    }
    }

    const isPasswordValid = () => {
      return passwordValue && passwordValue === confirmPasswordValue;
    }

  const doReset = () => {

    const params = new URLSearchParams(history.location.search);
    const token = params.get('token');
    if (!token) {
      authError("There are no token")(userDispatch)
    }

    if (!isPasswordValid()) {
      checkPassword();
    } else {
      resetPassword(token, passwordValue, history)(userDispatch);
    }
  }


  return (
    <Grid container className={classes.container}>
      <div className={classes.logotypeContainer}>
        <img src={logo} alt="logo" className={classes.logotypeImage} />
        <Typography className={classes.logotypeText}>Material Admin</Typography>
      </div>
      <div className={classes.customFormContainer}>
        <div className={classes.form}>
        <Input
            id="password"
            InputProps={{
              classes: {
                underline: classes.InputUnderline,
                input: classes.Input
              }
            }}
            value={passwordValue}
            onChange={e => setPasswordValue(e.target.value)}
            margin="normal"
            placeholder="password"
            type="password"
            fullWidth
          />
          <Input
            id="confirmPassword"
            InputProps={{
              classes: {
                underline: classes.InputUnderline,
                input: classes.Input
              }
            }}
            value={confirmPasswordValue}
            onChange={e => setConfirmPasswordValue(e.target.value)}
            margin="normal"
            placeholder="Confirm Password"
            type="password"
            fullWidth
          />
          <div className={classes.formButtons}>
            {isLoading ? (
              <CircularProgress size={26} className={classes.loginLoader} />
            ) : (
              <Button
                disabled={
                  (passwordValue.length === 0 || confirmPasswordValue.length === 0) || (passwordValue !== confirmPasswordValue)
                }
                onClick={() => doReset()}
                variant="contained"
                color="primary"
                size="large"
              >
                reset password
              </Button>
            )}
            <Button
              color="primary"
              size="large"
              onClick={() => history.push('/login')}
              className={classes.forgetButton}
            >
              Back to login
            </Button>
          </div>
        </div>
        <Typography color="primary" className={classes.copyright}>
          Â© 2014-2020 Flatlogic, LLC. All rights reserved.
        </Typography>
      </div>
    </Grid>
  );
}

export default withRouter(Reset);
