import React from "react";
import Unity from "react-unity-webgl";
import Iframe from 'react-iframe';

import classnames from 'classnames'

import { Grid, Box, Fab, IconButton} from "@material-ui/core";
import useStyles from "./styles";
import Icon from '@mdi/react'

import {
  Menu as MenuIcon,
  MailOutline as MailIcon,
  NotificationsNone as NotificationsIcon,
  Person as AccountIcon,
  Search as SearchIcon,
  Send as SendIcon,
  ArrowBack as ArrowBackIcon
} from "@material-ui/icons";


// components
import Widget from "../../components/Widget";
import Code from "../../components/Code";
import { Typography } from "../../components/Wrappers";
import PlotPopper from './components/PlotPopper';
import Sidebar from '../../components/Sidebar'
import structure from './components/SidebarStructure'

//import { useLayoutState } from '../../context/LayoutContext'
import {
  useLayoutState,
  useLayoutDispatch,
  toggleSidebar
} from "../../context/LayoutContext";

import {
    mdiSettings as SettingsIcon,
    mdiChartLine as TimelineIcon
} from '@mdi/js'


import classNames from "classnames";


const fabStyle = {
    right: 0,
    position: 'fixed',
    zIndex: 100
};

export default function Module (props) {
//  const unityContext = new UnityContext({
//  loaderUrl: "http://159.93.221.48/test/Build/UnityLoader.js",
//  dataUrl: "http://159.93.221.48/test/Build/Venv.data.unityweb",
// / frameworkUrl: "http://159.93.221.48/test/Build/Venv.wasm.framework.unityweb",
//  codeUrl: "Venv.wasm.code.unityweb",
//});

//let unityContent = new UnityContent(
//        "http://159.93.221.48/test/Build/Venv.json",
//        "http://159.93.221.48/test/Build/UnityLoader.js"
//    );

  const classes = useStyles();
var layoutState = useLayoutState();
  var layoutDispatch = useLayoutDispatch();
//  var userDispatch = useUserDispatch();
 // const managementDispatch = useManagementDispatch();

//   const [isSmall, setSmall] = useState(false);

    const module_id = props.match.params.id

    const [anchorEl, setAnchorEl] = React.useState(null)
    const open = Boolean(anchorEl)
    const id = open ? 'add-section-popover' : undefined
    const handleClick = event => {
        setAnchorEl(open ? null : event.currentTarget)
    }
  if (module_id == 1) {
  return (
    <div className={classes.root}>

    <div className={classnames(classes.content, {[classes.contentShift]: false })} >
                     <div className={classes.fakeToolbar} />

     <Fab
                    color="primary"
                    aria-label="settings"
                    onClick={e => handleClick(e)}
                    className={classes.changeThemeFab}
                    style={fabStyle}
                >
                    <Icon path={TimelineIcon} size={1} color="#fff" />
                </Fab>
      <PlotPopper
                    id={id}
                    open={open}
                    anchorEl={anchorEl}
                />

      <Grid container spacing={6}>
        <Grid item xs={12} md={12}>
          <Widget title="Знакомство с мобильной платформой" disableWidgetMenu>

            <Typography block>
В этом вводном уроке мы познакомимся с возможностями мобильного платформы (робота). Слово «робот» впервые использовал писатель Карел Чапек для обозначения искусственных людей в пьесе «Россумские универсальные роботы», так в пьесе назывались искусственные создания, эксплуатируемые людьми. Современные роботы - это автоматические механизмы, предназначенные для решения различных задач (в зависимости от компонентов, установленных на робота). Любого робота можно условно разделить на следующие части: 
<ul><li>система принятия решений («мозг» робота);</li><li>измерительная система (сенсоры);</li><li>исполнительная система (актуаторы);</li><li>система вывода информации;</li><li>система электропитания;</li><li>система связи;</li><li>механика робота (части, скрепляющие все системы в целом, корпус робота).</li></ul>
            </Typography>
            <Widget ctitle="Code example" lassName={classes.paper} disableWidgetMenu>
              <Code>
                {`
  <Widget title="Simple Widget" disableWidgetMenu>
    Lorem ipsum dolor sit amet consectetur, adipisicing elit. A,
    rem?
  </Widget>
              `}
              </Code>
            </Widget>
          </Widget>
        </Grid>
      </Grid>
   </div>
   </div>

  );
}

return (
    <>
<Grid container spacing={2}>
        <Grid item md={5}>
<Iframe url="http://159.93.221.48/venv"
        width="400px"
        height="600px"
        id="myId"
        className="myClassname"
        display="initial"
        position="relative"/>
        </Grid>
   <Grid item md={7}>
<Iframe url="http://79.143.25.41/eqvium"
        width="650px"
        height="600px"
        id="myId"
        className="myClassname"
        display="initial"
        position="relative"/>
        </Grid>

</Grid>
    </>
);

}
