//import React from "react";
import React, { useState, useEffect, useRef } from "react";

import {
  Grid,
  Box,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  Card,
  CardActions,
  CardContent,
  CardActionArea,
  CardMedia,
  Link
} from "@material-ui/core";
import { Star as StarIcon } from "@material-ui/icons";
import { yellow } from "@material-ui/core/colors/index";
import useStyles from "./styles";

//components
import { Typography, Chip } from "../../components/Wrappers";

import img from "../../images/img1.jpg";


//products array
import { rows } from "./mock";
import axios from "axios";

const Hardware = props => {
  const typeRef = React.useRef(null);
  const brandsRef = React.useRef(null);
  const sizeRef = React.useRef(null);
  const colourRef = React.useRef(null);
  const rangeRef = React.useRef(null);
  const sortRef = React.useRef(null);

  const ws = useRef(null);

  const rows = [
  {
    id: 0,
    dev_sn: "test",
    stat: "offline"
  },
  {
    id: 1,
    dev_sn: "Событие",
    stat: "offline"
  }
];


  const [rows2, updateData2] = useState([]);

//  const rows2 = [];

  const classes = useStyles();


  useEffect(() => {
        ws.current = new WebSocket("ws://79.143.25.41:8080/gear");
        ws.current.binaryType = 'arraybuffer';
        ws.current.onopen = () => {
          console.log("ws opened");
          axios.defaults.headers.common["Authorization"] = "";
          axios.get("/hardware/?owner="+localStorage.getItem("user")).then(res => { 
            console.log(res.data);
            updateData2(res.data);
            var len = res.data.length;
            let array2 = [];
            for (var i = 0; i < len; i++) {
               array2[i] = {"dev_sn":res.data[i].dev_sn, "stat":"offline"};
//               rows2[i]res.data[i].dev_sn] = "offline";
            }
            updateData2(array2);
           ////array2[0] =  { "id":0, "dev_sn":"23333", "stat":"offline"};
            //console.log(array2);
            //updateData2(array2);
            //console.log(JSON.stringify(res.data));
            ws.current.send(JSON.stringify(res.data));}).catch(err => console.error(err));
        }
        ws.current.onclose = () => console.log("ws closed"); //devs - offline
        return () => {
            ws.current.close();
        };
    }, []);

    useEffect(() => {
        if (!ws.current) return;
        ws.current.onmessage = e =>  {
//          console.log(e.data);
            if(e.data != null) {
            var obj = JSON.parse(e.data);
           console.log(obj.device, obj.online, "change");
           var len = rows2.length;
            let array2 = rows2;
            for (var i = 0; i < len; i++) {
               if(array2[i].dev_sn == obj.device) {
                  var isonline;
                  if(obj.online === "true") { isonline = "online";}
                  if(obj.online === "false") { isonline = "offline";}
                  array2[i] = {"dev_sn":obj.device, "stat":isonline};
               }
//               rows2[i]res.data[i].dev_sn] = "offline";
            }
             console.log(array2);
            //updateData2(array2);
//           if(obj.online === "true") { rows2[obj.device] = "online";}
//           if(obj.online === "false") { rows2[obj.device] = "offline";}
  //         console.log(rows2);
        }
     };
    }, []);


          //ws.current.send("hi");

  return (
    <>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Box display="flex">
          </Box>
        </Grid>
        <Grid item xs={12}>
          <Box display={"flex"} flexWrap={"wrap"}>
            <Grid container item spacing={3}>
{rows2.map(c => (
   <Typography gutterBottom variant="h5" component="h2">
                          {c.dev_sn}  {c.stat}
                        </Typography>
              ))}

            </Grid>
          </Box>
        </Grid>
      </Grid>
    </>
  );
};

export default Hardware;
