import React, { useState, useEffect, useRef } from "react";
import { Grid } from "@material-ui/core";
import { useTheme } from "@material-ui/styles";
import { Typography, Button } from '../../components/Wrappers'
import ReactApexChart from "react-apexcharts";
import ApexCharts from "apexcharts";
import ApexLineChart from "./components/ApexLineChart";

// components
import Widget from "../../components/Widget/Widget";



export default function Charts(props) {

  const ws = useRef(null);
  const [data1, updateData1] = useState([0,0,0,0,0,0,0]);
  //const [data1, updateData1] = useState([0,0,0]);
  //const [data2, updateData2] = useState([0,0,0,0,0,0,0]);

  useEffect(() => {
        ws.current = new WebSocket("ws://79.143.25.41:8080/gear");
        ws.current.onopen = () => console.log("ws opened");
        ws.current.onclose = () => console.log("ws closed");

        return () => {
            ws.current.close();
        };
    }, []);

    useEffect(() => {
        if (!ws.current) return;

        ws.current.onmessage = e => {
            //const message = JSON.parse(e.data);
            //console.log("ws recieved: ", JSON.parse(e.data));
            ////if (!isNaN(parseInt(e.data))) {
            ////  let array1 = [...data1, parseInt(e.data)];
              //console.log(parseInt(e.data));
              ////array1.shift();
              ////updateData1(array1);
              ////console.log("ws recieved: ", data1);
            ////}
            var str = '{\"data\" : \"slot\"}'
            console.log("ws recieved: ", JSON.parse(str));
        };
    }, [data1]);


  // local

  return (
    <>
      <Grid container spacing={4}>
        <Grid item md={6} xs={12}>
          <Widget title={"Поток данных"} noBodyPadding  button="Set Stream">
            <ApexLineChart
              data={data1}
              type="line"
              height="350"
            />
          </Widget>
        </Grid>
        <Grid item md={6} xs={12}>
<Button
                      variant="contained"
                      color="secondary"
                  >
 Set Stream
                  </Button>
        </Grid>
        <Grid item md={6} xs={12}>
        </Grid>
        <Grid item md={6} xs={12}>
        </Grid>
      </Grid>
    </>
  );
}
