import React from "react";
import {
  Grid,
  FormHelperText,
  InputAdornment,
  Divider,
  MenuItem,
  Select
} from "@material-ui/core";
import { withStyles } from "@material-ui/core/styles";
import useStyles from "./styles";
import cn from "classnames";
import Checkbox from "@material-ui/core/Checkbox";
import Radio from "@material-ui/core/Radio";
import Input from "@material-ui/core/TextField";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Favorite from "@material-ui/icons/Favorite";
import FavoriteBorder from "@material-ui/icons/FavoriteBorder";
import CheckBoxOutlineBlankIcon from "@material-ui/icons/CheckBoxOutlineBlank";
import CheckBoxIcon from "@material-ui/icons/CheckBox";
import RadioButtonUncheckedIcon from "@material-ui/icons/RadioButtonUnchecked";
import RadioButtonCheckedIcon from "@material-ui/icons/RadioButtonChecked";
import {
  AccountCircle,
  VpnKey as PasswordIcon,
  AccountBalanceWallet as WalletIcon
} from "@material-ui/icons";

// components
import Widget from "../../../components/Widget/Widget";
import { Button, Typography } from "../../../components/Wrappers/";
import Code from "../../../components/Code";

const GreenCheckbox = withStyles({
  root: {
    color: "green",
    "&$checked": {
      color: "green"
    }
  }
})(props => <Checkbox color="default" {...props} />);

const GreenRadio = withStyles({
  root: {
    color: "green",
    "&$checked": {
      color: "green"
    }
  }
})(props => <Radio color="default" {...props} />);

const SuccessInput = withStyles(theme => ({
  root: {
    "& .MuiInputLabel-root": {
      color: "green"
    },
    "& .MuiOutlinedInput-root": {
      "& fieldset": {
        borderColor: "green"
      },
      "&:hover fieldset": {
        borderColor: "green"
      },
      "&.Mui-focused fieldset": {
        borderColor: "green",
        borderWidth: 2
      }
    }
  }
}))(props => <Input {...props} />);

const actions = [
  {
    id: 0,
    value: "Action"
  },
  {
    id: 1,
    value: "Another action"
  },
  {
    id: 2,
    value: "Something else here"
  },
  {
    id: 3,
    type: "divider"
  },
  {
    id: 4,
    value: "Separated link"
  }
];

export default function FormsElements() {
  const [isChecked, setChecked] = React.useState({
    default: false,
    horizontal: false
  });
  const [action, setAction] = React.useState("Action");

  const handleChange = e => {
    setAction(e.target.value);
  };

  const classes = useStyles();

  function StyledRadio(props) {
    return (
      <Radio
        className={classes.root}
        disableRipple
        color="default"
        checkedIcon={<span className={cn(classes.icon, classes.checkedIcon)} />}
        icon={<span className={classes.icon} />}
        {...props}
      />
    );
  }
  return (
    <div>
      <Grid container spacing={6}>
        <Grid item md={6} xs={12}>
          <Widget
            title="Horizontal form"
            bodyClass={classes.horizontalFormTop}
            disableWidgetMenu
            inheritHeight
          >
            <Grid container direction={"column"} spacing={3}>
              <Grid item container alignItems={"center"}>
                <Grid item xs={6}>
                  <Typography variant={"body1"}>Normal field</Typography>
                </Grid>
                <Grid xs={6} item>
                  <Input
                    id="component-helper1"
                    placeholder={"May have placeholder"}
                    style={{ width: "100%" }}
                  />
                </Grid>
              </Grid>
              <Grid item container alignItems={"center"}>
                <Grid item xs={6}>
                  <Typography variant={"body1"}>Label hint</Typography>
                </Grid>
                <Grid xs={6} item>
                  <Input
                    id="component-helper2"
                    aria-describedby="component-helper-text"
                    style={{ width: "100%" }}
                  />
                  <FormHelperText id="component-helper-text">
                    Some important helper text
                  </FormHelperText>
                </Grid>
              </Grid>
              <Grid item container alignItems={"center"}>
                <Grid item xs={6}>
                  <Typography variant={"body1"}>Disabled input</Typography>
                </Grid>
                <Grid item xs={6}>
                  <Input
                    disabled
                    defaultValue={"Default value"}
                    id="component-helper3"
                    style={{ width: "100%" }}
                  />
                </Grid>
              </Grid>
              <Grid item container alignItems={"center"}>
                <Grid item xs={6}>
                  <Typography variant={"body1"}>Max length</Typography>
                </Grid>
                <Grid item xs={6}>
                  <Input
                    id="component-helper4"
                    inputProps={{ maxLength: 3 }}
                    style={{ width: "100%" }}
                  />
                </Grid>
              </Grid>
              <Grid item container alignItems={"center"}>
                <Grid item xs={6}>
                  <Typography variant={"body1"}>Prepended input</Typography>
                </Grid>
                <Grid item xs={6}>
                  <Input
                    id="component-helper5"
                    InputProps={{
                      startAdornment: (
                        <InputAdornment position="start">
                          <AccountCircle />
                        </InputAdornment>
                      )
                    }}
                    style={{ width: "100%" }}
                  />
                </Grid>
              </Grid>
              <Grid item container alignItems={"center"}>
                <Grid item xs={6}>
                  <Typography variant={"body1"}>Password</Typography>
                </Grid>
                <Grid item xs={6}>
                  <Input
                    id="component-helper6"
                    InputProps={{
                      startAdornment: (
                        <InputAdornment position="start">
                          <PasswordIcon />
                        </InputAdornment>
                      )
                    }}
                    style={{ width: "100%" }}
                  />
                </Grid>
              </Grid>
              <Grid item container alignItems={"center"}>
                <Grid item xs={6}>
                  <Typography variant={"body1"}>Appended input</Typography>
                </Grid>
                <Grid item xs={6}>
                  <Input
                    id="component-helper7"
                    InputProps={{
                      endAdornment: (
                        <InputAdornment position="start">.00</InputAdornment>
                      )
                    }}
                    style={{ width: "100%" }}
                  />
                </Grid>
              </Grid>
              <Grid item container alignItems={"center"}>
                <Grid item xs={6}>
                  <Typography variant={"body1"}>Combined input</Typography>
                </Grid>
                <Grid item xs={6}>
                  <Input
                    id="component-helper8"
                    InputProps={{
                      startAdornment: (
                        <InputAdornment position="start">
                          <WalletIcon />
                        </InputAdornment>
                      ),
                      endAdornment: (
                        <InputAdornment position="start">.00</InputAdornment>
                      )
                    }}
                    style={{ width: "100%" }}
                  />
                </Grid>
              </Grid>
              <Grid item container>
                <Grid item>
                  <Button
                    variant={"contained"}
                    color={"primary"}
                    style={{ marginRight: 8 }}
                  >
                    Save changes
                  </Button>
                </Grid>
                <Grid item>
                  <Button variant={"contained"} color={"secondary"}>
                    Cancel
                  </Button>
                </Grid>
              </Grid>
            </Grid>
          </Widget>
        </Grid>
        <Grid item md={6} xs={12}>
          <Widget title="Default Form" disableWidgetMenu inheritHeight>
            <Grid container direction={"column"}>
              <Grid item>
                <Typography variant={"body1"}>Search type input</Typography>
                <Input
                  color={"primary"}
                  type={"search"}
                  placeholder={"Search"}
                  fullWidth
                />
              </Grid>
              <Grid item>
                <Typography variant={"body1"} style={{ marginTop: 16 }}>
                  Outlined input
                </Typography>
                <Input
                  id="outlined-input"
                  margin="normal"
                  variant={"outlined"}
                />
              </Grid>
              <Grid item>
                <Typography style={{ marginTop: 16 }}>Filled input</Typography>
              </Grid>
              <Grid item>
                <Input id="outlined-input" margin="normal" variant={"filled"} />
              </Grid>
            </Grid>
          </Widget>
        </Grid>
      </Grid>
      <Grid container spacing={6}>
        <Grid item md={6} xs={12}>
          <Widget title="Control sizing" disableWidgetMenu inheritHeight>
            <Grid container direction={"column"}>
              <Grid item>
                If{" "}
                <Code row inline>
                  dense
                </Code>
                or{" "}
                <Code row inline>
                  normal
                </Code>
                , will adjust vertical spacing of this and contained components.
                <Input
                  label='margin="dense"'
                  placeholder={"Margin dense"}
                  margin="dense"
                  variant="outlined"
                  fullWidth
                />
              </Grid>
              <Grid item>
                <Input
                  label='margin="normal"'
                  placeholder={"Margin normal"}
                  margin="normal"
                  variant="outlined"
                  fullWidth
                />
              </Grid>
            </Grid>
          </Widget>
        </Grid>
        <Grid item xs={12} md={6}>
          <Widget title="Selects" disableWidgetMenu inheritHeight>
            <Grid item container alignItems={"center"}>
              <Grid item xs={6}>
                <Typography variant={"body1"}>Default select</Typography>
              </Grid>
              <Grid xs={6} item>
                <Select
                  labelId="demo-simple-select-label"
                  id="demo-simple-select"
                  value={action}
                  onChange={handleChange}
                >
                  {actions.map(c =>
                    c.type === "divider" ? (
                      <Divider key={c.id} />
                    ) : (
                      <MenuItem value={c.value} key={c.id}>
                        {c.value}
                      </MenuItem>
                    )
                  )}
                </Select>
              </Grid>
            </Grid>
            <Grid item container alignItems={"center"}>
              <Grid item xs={6}>
