import React from "react";
import ApexCharts from "react-apexcharts";
import { useTheme } from "@material-ui/styles";



export default function ApexLineChart(props) {

  const options = {
    dataLabels : {
      enabled: false,
    },
    stroke: {
      curve: "stepline",
      width: 2,
    },
    markers: {
     size: 3,
     shape: "circle",
     colors: ["#008FFB"]
    },
    xaxis : {
    tickAmount: 15,
    min: 1,
    max: 299,
    },
    colors: [theme.palette.primary.main, theme.palette.success.main],
    chart: {
      toolbar: {
        show: false,
      },
    },
    legend: {
      show: false,
    },
  }

  const series = [
  {
    name: "row1",
    data: props.data
  }
  ];

  return (
    <ApexCharts
      options={options}
      series={series}
      type="line"
      height={350}
    />
  );
}
